    "type": "integer",
    "style": {
      "size": "medium",
      "color": "var(--text-color-secondary)"
    }
  },
  {
    "visualWeight": 0,
    "display": "text",
    "type": "integer",
    "style": {
      "size": "medium",
      "color": "var(--text-color-default)"
    }
  },
  {
    "visualWeight": 1,
    "display": "text",
    "type": "integer",
